import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import Sidebar from '../components/Sidebar';
import TweetList from '../components/TweetList';
import NewTweet from '../components/NewTweet';
import { db, collection, addDoc, onSnapshot } from '../firebase';
import './Home.css';

const Home = () => {
  const [tweets, setTweets] = useState([]);

  useEffect(() => {
    const unsubscribe = onSnapshot(collection(db, 'tweets'), (snapshot) => {
      const fetchedTweets = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
      }));
      setTweets(fetchedTweets);
    });

    return () => unsubscribe();
  }, []);

  const addTweet = async (content, image) => {
    await addDoc(collection(db, 'tweets'), {
      username: 'User',
      content,
      image,
      timestamp: new Date(),
      likes: 0,
      comments: []
    });
  };

  const likeTweet = async (id) => {
    const tweetRef = doc(db, 'tweets', id);
    const tweetDoc = await getDoc(tweetRef);
    await updateDoc(tweetRef, {
      likes: tweetDoc.data().likes + 1
    });
  };

  return (
    <div className="home-page">
      <Navbar />
      <Sidebar />
      <main className="main-content">
        <NewTweet addTweet={addTweet} />
        <TweetList tweets={tweets} likeTweet={likeTweet} />
      </main>
    </div>
  );
};

export default Home;
